# ----------------- Admin (optional) -----------------
# Jei norite, kad nick 'admin' reikalautÅ³ slaptaÅ¾odÅ¾io:
# Render -> Environment -> pridÄ—kite ADMIN_PASSWORD (pvz. labai stiprÅ³).
ADMIN_NICKS = {"admin"}  # case-insensitive palyginimas
ADMIN_PASSWORD = (os.environ.get("ADMIN_PASSWORD") or "").strip()

def is_admin_nick(nick: str) -> bool:
    return (nick or "").casefold() in {x.casefold() for x in ADMIN_NICKS}

def admin_pw_ok(pw: str) -> bool:
    if not ADMIN_PASSWORD:
        # jei ENV nepaduotas â€“ slaptaÅ¾odÅ¾io tikrinimas iÅ¡jungtas (kad neuÅ¾sirakintumÄ—te)
        return True
    return secrets.compare_digest((pw or ""), ADMIN_PASSWORD)

# ----------------- Validation -----------------
NICK_RE = re.compile(r"^[A-Za-z0-9Ä„ÄŒÄ˜Ä–Ä®Å Å²ÅªÅ½Ä…ÄÄ™Ä—Ä¯Å¡Å³Å«Å¾_\-\. ]{2,24}$")
ROOM_RE = re.compile(r"^[a-z0-9][a-z0-9_\-]{0,23}$")

def valid_nick(n: str) -> bool:
    return bool(NICK_RE.match((n or "").strip()))

def norm_room(room: str) -> str:
    return (room or "").strip().lstrip("#").lower()

def valid_room_key(r: str) -> bool:
    return bool(ROOM_RE.match(r))

