# ----------------- JSON helpers -----------------
def json_dumps(obj: Any) -> str:
    return json.dumps(obj, ensure_ascii=False, separators=(",", ":"))

def json_loads(s: Optional[str]) -> dict:
    if not s:
        return {}
    try:
        return json.loads(s)
    except Exception:
        return {}

# ----------------- DB (SQLite) -----------------
db_lock = asyncio.Lock()

def db() -> sqlite3.Connection:
    conn = sqlite3.connect(DB_PATH, check_same_thread=False)
    conn.row_factory = sqlite3.Row
    return conn

def db_init() -> None:
    conn = db()
    cur = conn.cursor()
    cur.execute(
        """
        CREATE TABLE IF NOT EXISTS messages (
          id INTEGER PRIMARY KEY AUTOINCREMENT,
          room TEXT NOT NULL,
          msg_type TEXT NOT NULL,     -- 'msg','sys','deleted'
          ts TEXT NOT NULL,
          nick TEXT,
          color TEXT,
          text TEXT,
          extra TEXT
        )
        """
    )
    cur.execute(
        """
        CREATE TABLE IF NOT EXISTS pins (
          room TEXT NOT NULL,
          msg_id INTEGER NOT NULL,
          PRIMARY KEY(room, msg_id)
        )
        """
    )
    cur.execute("CREATE INDEX IF NOT EXISTS idx_messages_room ON messages(room, id)")
    conn.commit()
    conn.close()

db_init()

async def db_insert_message(room: str, msg_type: str, ts_s: str, nick: Optional[str], color: Optional[str], text: str, extra: dict) -> int:
    async with db_lock:
        conn = db()
        cur = conn.cursor()
        cur.execute(
            "INSERT INTO messages(room,msg_type,ts,nick,color,text,extra) VALUES(?,?,?,?,?,?,?)",
            (room, msg_type, ts_s, nick, color, text, json_dumps(extra)),
        )
        conn.commit()
        msg_id = int(cur.lastrowid)
        conn.close()
        return msg_id

async def db_get_message(msg_id: int) -> Optional[sqlite3.Row]:
    async with db_lock:
        conn = db()
        cur = conn.cursor()
        cur.execute("SELECT * FROM messages WHERE id=?", (msg_id,))
        row = cur.fetchone()
        conn.close()
        return row

async def db_update_message(msg_id: int, text: str, msg_type: Optional[str] = None, extra: Optional[dict] = None) -> None:
    async with db_lock:
        conn = db()
        cur = conn.cursor()
        if msg_type is None and extra is None:
            cur.execute("UPDATE messages SET text=? WHERE id=?", (text, msg_id))
        elif msg_type is None:
            cur.execute("UPDATE messages SET text=?, extra=? WHERE id=?", (text, json_dumps(extra or {}), msg_id))
        elif extra is None:
            cur.execute("UPDATE messages SET msg_type=?, text=? WHERE id=?", (msg_type, text, msg_id))
        else:
            cur.execute("UPDATE messages SET msg_type=?, text=?, extra=? WHERE id=?", (msg_type, text, json_dumps(extra or {}), msg_id))
        conn.commit()
        conn.close()

async def db_load_history(room: str, limit: int) -> list[dict]:
    limit = max(1, min(int(limit), HISTORY_LIMIT))
    async with db_lock:
        conn = db()
        cur = conn.cursor()
        cur.execute(
            "SELECT * FROM messages WHERE room=? ORDER BY id DESC LIMIT ?",
            (room, limit),
        )
        rows = cur.fetchall()
        conn.close()
    items: list[dict] = []
    for r in reversed(rows):
        items.append(
            {
                "id": int(r["id"]),
                "type": r["msg_type"],
                "t": r["ts"],
                "nick": r["nick"],
                "color": r["color"],
                "text": r["text"] or "",
                "extra": json_loads(r["extra"]),
            }
        )
    return items

async def db_pin(room: str, msg_id: int) -> None:
    async with db_lock:
        conn = db()
        cur = conn.cursor()
        cur.execute("INSERT OR IGNORE INTO pins(room,msg_id) VALUES(?,?)", (room, msg_id))
        conn.commit()
        conn.close()

async def db_list_pins(room: str, limit: int = 50) -> list[int]:
    async with db_lock:
        conn = db()
        cur = conn.cursor()
        cur.execute("SELECT msg_id FROM pins WHERE room=? ORDER BY msg_id DESC LIMIT ?", (room, limit))
        rows = cur.fetchall()
        conn.close()
    return [int(x["msg_id"]) for x in rows]

