async def send_rooms_list(ws: WebSocket) -> None:
    u = all_users_by_ws.get(ws)
    if not u:
        return
    async with state_lock:
        keys = sorted(set(rooms.keys()) | set(DEFAULT_ROOMS.keys()))
        items = []
        for k in keys:
            r = ensure_room(k)
            items.append(
                {
                    "room": r.key,
                    "title": r.title,
                    "topic": r.topic,
                    "count": len(r.users),
                    "joined": (r.key in u.rooms),
                    "last_ts": r.last_ts,
                    "last_preview": r.last_preview,
                }
            )
    await ws_send(ws, {"type": "rooms", "t": ts(), "items": items})

async def broadcast_rooms_list_all() -> None:
    """IÅ¡siunÄia atnaujintÄ… kanalÅ³ sÄ…raÅ¡Ä… visiems prisijungusiems."""
    async with state_lock:
        sockets = list(all_users_by_ws.keys())
    for w in sockets:
        await send_rooms_list(w)


# ----------------- Rooms list live updates (debounced) -----------------
_rooms_dirty_task: Optional[asyncio.Task] = None

async def _rooms_dirty_worker():
    await asyncio.sleep(0.35)
    await broadcast_rooms_list_all()

def mark_rooms_dirty() -> None:
    global _rooms_dirty_task
    if _rooms_dirty_task and not _rooms_dirty_task.done():
        return
    _rooms_dirty_task = asyncio.create_task(_rooms_dirty_worker())

def update_room_activity(room_key: str, preview: str = "") -> None:
    key = norm_room(room_key) or "main"
    r = ensure_room(key)
    r.last_ts = time.time()
    if preview:
        r.last_preview = (preview or "").strip().replace("\n", " ")[:80]
    mark_rooms_dirty()

async def join_room(ws: WebSocket, room_key: str) -> Tuple[bool, str]:
    u = all_users_by_ws.get(ws)
    if not u:
        return False, "no_user"

    key = norm_room(room_key)
    if not valid_room_key(key):
        return False, "bad_room"

    async with state_lock:
        r = ensure_room(key)
        if key in u.rooms:
            return True, "already"
        u.rooms.add(key)
        r.clients.add(ws)
        r.users[ws] = u

    await ws_send(ws, {"type": "topic", "room": key, "t": ts(), "text": f"{ensure_room(key).title} â€” {ensure_room(key).topic}"})
    hist = await db_load_history(key, JOIN_HISTORY_DEFAULT)
    await ws_send(ws, {"type": "history", "room": key, "t": ts(), "items": hist})
    await send_rooms_list(ws)
    await broadcast_userlist(key)
    await broadcast_rooms_list_all()
    return True, "ok"

async def leave_room(ws: WebSocket, room_key: str) -> Tuple[bool, str]:
    u = all_users_by_ws.get(ws)
    if not u:
        return False, "no_user"

    key = norm_room(room_key)

    async with state_lock:
        if key not in u.rooms:
            return False, "not_member"

        # Leisk iÅ¡eiti iÅ¡ bet kurio kanalo, bet palik bent vienÄ… prisijungtÄ… kanalÄ… Å¡iai sesijai.
        if len(u.rooms) <= 1:
            return False, "deny_last_room"

        u.rooms.discard(key)
        r = ensure_room(key)
        r.clients.discard(ws)
        r.users.pop(ws, None)

        if u.active_room == key:
            # Parenkame naujÄ… aktyvÅ³ kanalÄ… iÅ¡ likusiÅ³ prisijungtÅ³.
            if "main" in u.rooms:
                u.active_room = "main"
            else:
                u.active_room = sorted(u.rooms)[0] if u.rooms else "main"

    await send_rooms_list(ws)
    await broadcast_userlist(key)
    await broadcast_rooms_list_all()
    return True, "ok"


async def admin_delete_room(ws: WebSocket, room_arg: str) -> Tuple[bool, str]:
    """Admin: delete a non-default room if it is empty (0 online)."""
    u = all_users_by_ws.get(ws)
    if not u:
        return False, "forbidden"
    if not is_admin_nick(u.nick):
        return False, "forbidden"

    key = norm_room(room_arg)
    if not key:
        return False, "bad_room"
    if key in DEFAULT_ROOMS:
        return False, "default_room"
    # Never allow deleting DM/PM rooms via this command.
    if key.startswith("dm_") or key.startswith("dm-") or key.startswith("pm_") or key.startswith("pm-"):
        return False, "forbidden"

    # Delete only when room is truly empty.
    async with state_lock:
        r = rooms.get(key)
        if not r:
            return False, "not_found"
        if len(r.users) > 0 or len(getattr(r, "clients", set())) > 0:
            return False, "not_empty"

        # Remove from registry
        try:
            del rooms[key]
        except KeyError:
            return False, "not_found"

        # Defensive: remove from any user room-sets (should be none if empty)
        for uu in all_users_by_ws.values():
            try:
                uu.rooms.discard(key)
            except Exception:
                pass

        try:
            mark_rooms_dirty()
        except Exception:
            pass

    # Notify all clients to refresh rooms (and counts).
    try:
        await broadcast_rooms_list_all()
    except Exception:
        pass
    try:
        await broadcast_global_count()
    except Exception:
        pass

    return True, "ok"

async def focus_room(ws: WebSocket, room_key: str) -> None:
    u = all_users_by_ws.get(ws)
    if not u:
        return

    key = norm_room(room_key) or "main"
    if key not in u.rooms:
        ok, code = await join_room(ws, key)
        if not ok:
            await ws_send(ws, {"type": "sys", "t": ts(), "text": "Netinkamas kanalas."})
            return

    u.active_room = key
    r = ensure_room(key)
    await ws_send(ws, {"type": "topic", "room": key, "t": ts(), "text": f"{r.title} â€” {r.topic}"})
    await ws_send(ws, {"type": "users", "room": key, "t": ts(), "items": room_userlist(key)})
    hist = await db_load_history(key, JOIN_HISTORY_DEFAULT)
    await ws_send(ws, {"type": "history", "room": key, "t": ts(), "items": hist})

async def disconnect_ws(ws: WebSocket) -> None:
    async with state_lock:
        u = all_users_by_ws.pop(ws, None)
        if u:
            all_ws_by_nick_cf.pop(u.nick.casefold(), None)
            for rk in list(u.rooms):
                r = ensure_room(rk)
                r.clients.discard(ws)
                r.users.pop(ws, None)

    try:
        await ws.close()
    except Exception:
        pass

    if u:
        for rk in list(u.rooms):
            await broadcast_userlist(rk)
        await broadcast_rooms_list_all()
        await broadcast_global_count()

def check_rate_limit(u: User, msg_len: int) -> Tuple[bool, str]:
    now = time.time()
    while u.msg_events and now - u.msg_events[0][0] > 10:
        u.msg_events.popleft()

    count = len(u.msg_events)
    chars = sum(c for _, c in u.msg_events)

    if count >= MAX_MSG_PER_10S:
        return False, "rate"
    if chars + msg_len > MAX_CHARS_PER_10S:
        return False, "rate_chars"

    u.msg_events.append((now, msg_len))
    return True, "ok"

async def heartbeat(ws: WebSocket) -> None:
    try:
        while True:
            await asyncio.sleep(25)
            await ws_send(ws, {"type": "ping", "t": ts()})
    except Exception:
        return

