async def cmd_topic(ws: WebSocket, room_key: str, arg: Optional[str]) -> None:
    u = all_users_by_ws.get(ws)
    if not u:
        return
    r = ensure_room(room_key)
    if not arg:
        await ws_send(ws, {"type": "sys", "t": ts(), "text": f"Tema {r.title}: {r.topic}"})
