async def handle_command(ws: WebSocket, u: User, active_room: str, text: str) -> bool:
    t0 = text.strip()
    low = t0.lower()

    if low in ("/help", "/?"):
        await ws_send(ws, {"type": "sys", "t": ts(), "text": HELP_TEXT})
        return True

    if low == "/rooms":
        await cmd_rooms(ws)
        return True

    if low.startswith("/join "):
        arg = t0.split(" ", 1)[1].strip()
        ok, code = await join_room(ws, arg)
        if not ok and code == "bad_room":
            await ws_send(ws, {"type": "sys", "t": ts(), "text": "Netinkamas kanalo pavadinimas. Pvz: #main, #games"})
        else:
            await ws_send(ws, {"type": "sys", "t": ts(), "text": f"Prisijungei prie #{norm_room(arg)}."})
        return True

    if low.startswith("/leave "):
        arg = t0.split(" ", 1)[1].strip()
        ok, code = await leave_room(ws, arg)
        if not ok and code == "deny_last_room":
            await ws_send(ws, {"type": "sys", "t": ts(), "text": "Negali palikti paskutinio kanalo. Prisijunk prie kito kanalo ir bandyk dar kartÄ…."})
        elif not ok and code == "not_member":
            await ws_send(ws, {"type": "sys", "t": ts(), "text": "Tu nesi Å¡iame kanale."})
        else:
            await ws_send(ws, {"type": "sys", "t": ts(), "text": f"Palikai #{norm_room(arg)}."})
        return True

    if low.startswith("/roomdel ") or low.startswith("/room_delete ") or low.startswith("/roomdelete "):
        arg = t0.split(" ", 1)[1].strip()
        ok, code2 = await admin_delete_room(ws, arg)
        if not ok and code2 == "forbidden":
            await ws_send(ws, {"type": "sys", "t": ts(), "text": "Å iÄ… komandÄ… gali naudoti tik admin."})
        elif not ok and code2 == "bad_room":
            await ws_send(ws, {"type": "sys", "t": ts(), "text": "Netinkamas kanalo pavadinimas."})
        elif not ok and code2 == "default_room":
            await ws_send(ws, {"type": "sys", "t": ts(), "text": "BaziniÅ³ kanalÅ³ trinti negalima."})
        elif not ok and code2 == "not_empty":
            await ws_send(ws, {"type": "sys", "t": ts(), "text": "Kanalo trinti negalima, kol jame yra Å¾moniÅ³ (0 online reikalavimas)."})
        elif not ok and code2 == "not_found":
            await ws_send(ws, {"type": "sys", "t": ts(), "text": "Kanalo rasti nepavyko."})
        else:
            await ws_send(ws, {"type": "sys", "t": ts(), "text": f"Kanalas #{norm_room(arg)} iÅ¡trintas."})
        return True

    if low == "/who":
        await cmd_who(ws, active_room)
        return True

    if low.startswith("/topic"):
        parts = t0.split(" ", 1)
        arg = parts[1] if len(parts) == 2 else None
        await cmd_topic(ws, active_room, arg)
        return True

    if low.startswith("/history"):
        n = JOIN_HISTORY_DEFAULT
        parts = t0.split(" ", 1)
        if len(parts) == 2:
            try:
                n = int(parts[1].strip())
            except Exception:
                n = JOIN_HISTORY_DEFAULT
        await cmd_history(ws, active_room, n)
        return True

    if low == "/time":
        await ws_send(ws, {"type": "sys", "t": ts(), "text": f"Serverio laikas (Vilnius): {ts()}"})
        return True

    if low.startswith("/pin "):
        try:
            mid = int(t0.split(" ", 1)[1].strip())
        except Exception:
            await ws_send(ws, {"type": "sys", "t": ts(), "text": "Naudojimas: /pin ID"})
            return True
        await cmd_pin(ws, active_room, mid)
        return True

    if low == "/pins":
        await cmd_pins(ws, active_room)
        return True

    if low.startswith("/quote "):
        try:
            mid = int(t0.split(" ", 1)[1].strip())
        except Exception:
            await ws_send(ws, {"type": "sys", "t": ts(), "text": "Naudojimas: /quote ID"})
            return True
        await cmd_quote(ws, active_room, mid)
        return True

    if low.startswith("/edit "):
        parts = t0.split(" ", 2)
        if len(parts) < 3:
            await ws_send(ws, {"type": "sys", "t": ts(), "text": "Naudojimas: /edit ID NAUJAS_TEKSTAS"})
            return True
        try:
            mid = int(parts[1].strip())
        except Exception:
            await ws_send(ws, {"type": "sys", "t": ts(), "text": "Naudojimas: /edit ID NAUJAS_TEKSTAS"})
            return True
        await cmd_edit(ws, active_room, mid, parts[2])
        return True

    if low.startswith("/del "):
        try:
            mid = int(t0.split(" ", 1)[1].strip())
        except Exception:
            await ws_send(ws, {"type": "sys", "t": ts(), "text": "Naudojimas: /del ID"})
            return True
        await cmd_del(ws, active_room, mid)
        return True

    if low.startswith("/react "):
        parts = t0.split(" ", 2)
        if len(parts) < 3:
            await ws_send(ws, {"type": "sys", "t": ts(), "text": "Naudojimas: /react ID ðŸ˜€"})
            return True
        try:
            mid = int(parts[1].strip())
        except Exception:
            await ws_send(ws, {"type": "sys", "t": ts(), "text": "Naudojimas: /react ID ðŸ˜€"})
            return True
        await cmd_react(ws, active_room, mid, parts[2])
        return True


    if low.startswith("/mute "):
        parts = t0.split()
        if len(parts) < 2:
            await ws_send(ws, {"type": "sys", "t": ts(), "text": "Naudojimas: /mute nick [minutÄ—s]"})
            return True
        target = parts[1].lstrip("@")
        mins = 10
        if len(parts) >= 3:
            try:
                mins = int(parts[2])
            except Exception:
                mins = 10
        ok3, c3 = await cmd_mute(ws, u, target, mins)
        if not ok3 and c3 == "forbidden":
            await ws_send(ws, {"type": "sys", "t": ts(), "text": "Å iÄ… komandÄ… gali naudoti tik admin."})
        elif not ok3 and c3 == "bad_nick":
            await ws_send(ws, {"type": "sys", "t": ts(), "text": "Netinkamas nick."})
        return True


    return False

