@app.websocket("/ws")
async def ws_endpoint(ws: WebSocket):
    nick = (ws.query_params.get("nick") or "").strip()
    pw = (ws.query_params.get("pw") or "").strip()

    await ws.accept()
    peer = f"{ws.client.host if ws.client else 'unknown'}"
    log.info("WS accepted %s nick=%r", peer, nick)

    if not valid_nick(nick):
        await ws_send(ws, {"type": "error", "code": "BAD_NICK", "text": "Netinkamas nick."})
        await ws.close()
        return

    for rk in DEFAULT_ROOMS.keys():
        ensure_room(rk)

    async with state_lock:
        cf = nick.casefold()
        if cf in all_ws_by_nick_cf:
            await ws_send(ws, {"type": "error", "code": "NICK_TAKEN", "text": "Nick uÅ¾imtas."})
            await ws.close()
            return

        used = {u.color for u in all_users_by_ws.values()}
        color = alloc_color(used)

        u = User(nick=nick, color=color)
        all_users_by_ws[ws] = u
        all_ws_by_nick_cf[cf] = ws

    await broadcast_global_count()

    hb_task: Optional[asyncio.Task] = asyncio.create_task(heartbeat(ws))

    await join_room(ws, "main")
    await focus_room(ws, "main")
    await ws_send(ws, {"type": "me", "t": ts(), "nick": u.nick, "color": u.color})

    try:
        while True:
            msg = await ws.receive()
            mtype = msg.get("type")

            if mtype == "websocket.disconnect":
                raise WebSocketDisconnect

            if mtype != "websocket.receive":
                continue

            raw = msg.get("text") or msg.get("bytes")
            if raw is None:
                continue

            if isinstance(raw, (bytes, bytearray)):
                try:
                    raw = raw.decode("utf-8", errors="replace")
                except Exception:
                    raw = ""

            try:
                data = json.loads(raw)
            except Exception:
                continue

            if not isinstance(data, dict):
                continue

            if data.get("type") == "pong":
                continue

            if data.get("type") == "focus":
                await focus_room(ws, data.get("room", "main"))
                continue

            if data.get("type") == "say":
                room_key = norm_room(data.get("room", u.active_room or "main"))
                text = str(data.get("text", "")).strip()
                if not text:
                    continue
                mu = is_muted(u.nick)
                if mu:
                    await ws_send(ws, {"type": "sys", "t": ts(), "text": "Tu esi uÅ¾tildytas."})
                    continue


                ok, code = check_rate_limit(u, len(text))
                if not ok:
                    await ws_send(ws, {"type": "sys", "t": ts(), "text": "Per daug Å¾inuÄiÅ³ per trumpÄ… laikÄ…. Palauk kelias sekundes."})
                    continue

                if text.startswith("/"):
                    handled = await handle_command(ws, u, room_key, text)
                    if handled:
                        continue


                # DM: ensure recipient is joined so message is delivered and appears as unread.
                if is_dm_key(room_key):
                    async with state_lock:
                        sender_needs_join = room_key not in u.rooms
                    if sender_needs_join:
                        await join_room(ws, room_key)
                    await ensure_dm_recipient_joined(room_key, ws)

                extra = {"created_at": time.time(), "edited": False, "reactions": {}}
                msg_id = await db_insert_message(room_key, "msg", ts(), u.nick, u.color, text[:300], extra)
                update_room_activity(room_key, text)

                payload = {"type": "msg", "room": room_key, "id": msg_id, "t": ts(), "nick": u.nick, "color": u.color, "text": text[:300], "extra": extra}
                await room_broadcast(room_key, payload)
                continue

    except WebSocketDisconnect:
        log.info("WS disconnect %s nick=%r", peer, nick)
    except Exception:
        log.exception("WS error %s nick=%r", peer, nick)
    finally:
        if hb_task:
            hb_task.cancel()
        await disconnect_ws(ws)
        log.info("WS closed %s nick=%r", peer, nick)

